def pollard(n):
    a=2
    i=2
    while True:
        a=(a**i)%n
        d=gcd(a-1,n)
        if d>1:
            return d
        i+=1

def fermat(n):
    c=isqrt(n)

    while c<=n:
        d2=c*c-n
        if d2<0:
            continue
        d=isqrt(d2)
        if n==d*d:
            return d

